mapper.select:
SELECT * FROM ( SELECT ROWNUM r, q.* FROM(SELECT * FROM QUESTION
ORDER BY QUESTION_CREATE_DATE DESC
ORDER BY QUESTION_HIT DESC
ORDER BY QUESTION_REF DESC, QUESTION_STEP ASC
) q ) WHERE r BETWEEN #{startRow} AND #{lastRow}

mapper.update:
UPDATE	QUESTION
SET		QUESTION_TITLE = #{question_title}, QUESTION_CONTENTS = #{question_contents}
WHERE 	QUESTION_NUM = #{question_num}

mapper.insert:

  			SELECT BOARD_SEQ.NEXTVAL FROM DUAL

  		INSERT INTO QUESTION
  		(
	  		BOARD_SEQ.NEXTVAL, USER_ID, QUESTION_TITLE,QUESTION_CONTENTS,QUESTION_REF,QUESTION_STEP, QUESTION_DEPTH
  		) 
  		VALUES 
  		(
  			#{question_num}, #{user_id}, #{question_title},#{question_contents},#{question_ref}, #{question_step}, #{question_depth}
  		)
